import logo from './logo.svg';
import 'bulma/css/bulma.min.css';
import './App.css';
import {Heading, Icon} from 'react-bulma-components';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faEllipsisV, faSlidersH } from '@fortawesome/free-solid-svg-icons';
import { useState, useEffect } from 'react';
import React from 'react';


function Header(props) {
  const style = {
    "paddingTop": "100px"
  }

  return (
    <div>
      <p style={style} className="title is-1">Gym Cal</p>
      <hr/>
    </div>
    );
}

class DayCard extends React.Component {
  getClasses() {
    if (!this.props.state == "exclude") {return "card day-card"};
    return "card empty-card";
  };

  render() {
    return (
      <div className="column"> 
        <div className={this.getClasses()}>
          { !this.props.state == "exclude" ? 
            <div className="day-card-content">
              <div className="card-day-header">
                <div className="card-day level-left">
                  {this.props.day}
                </div>
              </div>
              <div className="card-main-content columns is-centered">
                <div className="workout-text"> L </div>
              </div>
              <div className="card-date-footer">
                {this.props.date}
              </div>
            </div>
          : null }
        </div>
      </div>
      );
  }
}

class WeekRow extends React.Component {
  render() {
    return (
        <div className="columns is-spaced week-row">
          {this.props.days.map((value, index) => {return <DayCard {...value}/>})}
        </div>
      )
  }
}

class Calendar extends React.Component {
  render () {
    return (
      <div className="calendar">
        <WeekRow days={[{date: "19", day:"Su", state: "exclude"}, {date: "20", day:"M", state: "exclude"}, {date: "21", day:"T", state: "exclude"}, {date: "22", day:"W", state: "exclude"}, {date: "23", day:"Th", state: "past"}, {date: "24", day:"F", state: "past"}, {date: "25", day:"S", state: "past"}]}/>
        <WeekRow days={[{date: "26", day:"Su"}, {date: "27", day:"M"}, {date: "28", day:"T"}, {date: "29", day:"W"}, {date: "30", day:"Th"}, {date: "31", day:"F"}, {date: "9/1", day:"S"}]}/>
        <WeekRow days={[{date: "2", day:"Su"}, {date: "3", day:"M"}, {date: "4", day:"T"}, {date: "5", day:"W"}, {date: "6", day:"Th"}, {date: "7", day:"F"}, {date: "7", day:"Sa"}]}/>
      </div>
    );
  }
}

function App() {
  return (
    <div className="App">
      <div className="container">
        {Header()}
        {<Calendar/>}
      </div>
    </div>
  );
}

export default App;
