import logo from './logo.svg';
import './App.css';
import {Heading, Icon} from 'react-bulma-components';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faEllipsisV, faSlidersH } from '@fortawesome/free-solid-svg-icons';
import { useState, useEffect } from 'react';
import 'bulma/css/bulma.min.css';
import React from 'react';


function Header(props) {
  const style = {
    "paddingTop": "100px"
  }

  return (
    <div>
      <p style={style} className="title is-1">Gym Cal</p>
      <hr/>
    </div>
    );
}

class DayCard extends React.Component {
  render() {
    return (
      <div className="column">
        <div className="card day-card">
          <header className="card-header">
            <p className="card-header-title">
              <span className="title is-6">{this.props.date}</span>
            </p>
            <button className="card-header-icon" aria-label="more options">
              <span className="icon">
                <i className="fas fa-angle-down" aria-hidden="true"></i>
              </span>
            </button>
          </header>
        </div>
      </div>
      );
  }
}

class WeekRow extends React.Component {
  render() {
    return (
        <div className="columns is-spaced">
          {this.props.days.map((value, index) => {return <DayCard date={value.date} day={value.day}/>})}
        </div>
      )
  }
}

class Calendar extends React.Component {
  render () {
    return (
      <WeekRow days={[{date: "9/1", day:"S"}, {date: "9/2", day:"M"}, {date: "9/3", day:"T"}, {date: "9/4", day:"W"}, {date: "9/5", day:"Th"}, {date: "9/6", day:"F"}, {date: "9/7", day:"F"}]}/>
    );
  }
}

function App() {
  return (
    <div className="App">
      <div className="container">
        {Header()}
        {<Calendar/>}
      </div>
    </div>
  );
}

export default App;
