import React from 'react';
import PropTypes from 'prop-types';

import parsePropsAsBulmaClasses from 'components/_utils/parsePropsAsBulmaClasses';
import Title from 'components/Title';

const Header = (props) => {

  const headerCSSClass = `header ${
    parsePropsAsBulmaClasses(props)
  }`;

  return (
    <header className={headerCSSClass}>
      <Title isH1>{props.children}</Title>
      {props.subtitle ? <Title isH2>{props.subtitle}</Title> : null}
    </header>
  );

};

Header.defaultProps = {
  children: null
};

Header.propTypes = {
  children: PropTypes.any
};

export default Header;
